import React, { useState } from 'react';
import ReactDOM, { render } from 'react-dom';
import './index.css';





// function App(props) {

//      return (<div className="header">
// {props.title}
//      </div>);

// }

// const App=({title})=>{
//        return (
//        <div className="header"> 
//       {title}
//      </div>);
// }

const testData = [
  // { name: "Dan Abramov", avatar_url: "https://avatars0.githubusercontent.com/u/810438?v=4", company: "@facebook" },
  // { name: "Sophie Alpert", avatar_url: "https://avatars2.githubusercontent.com/u/6820?v=4", company: "Humu" },
  // { name: "Sebastian Markbåge", avatar_url: "https://avatars2.githubusercontent.com/u/63648?v=4", company: "Facebook" },
];



function CardList(props) {
  return (<div>
    {/* <Card {...testData[0]}/>

    <Card {...testData[1]}/>  */}

    {props.profiles.map(x => <Card key={x.id}{...x} />)}

  </div>)
}

class Form extends React.Component {

 // userNameInput = React.createRef();
  state={userName:''};
  handleSubmit = async(evnt) => {
    evnt.preventDefault();
    //console.log(this.userNameInput.current.value);
   // console.log(this.state.userName);
    const resp=await fetch (`https://api.github.com/users/${this.state.userName}`);
    const data= await resp.json();
    console.log(data);
    this.props.onSubmitProfile(data);
    this.setState({userName:''});

  }

  onTextChange=(evnt)=>{
    this.setState({userName:evnt.target.value});
  }
  render() {
    return <form onSubmit={this.handleSubmit}>
       {/* return <form onSubmit={()=>this.props.onSubmit(this.state.userName)}> */}
      <input
        type="text"
        placeholder='GitHub username'
       // ref={this.userNameInput}
       value={this.state.userName}
       onChange={this.onTextChange}
        required>
      </input>
      <button>Add Card</button>
    </form>
  }
}
class Card extends React.Component {
  render() {
    const profile = this.props;
    return (<div className="github-profile" style={{ margin: '1rem' }}>
      <img src={profile.avatar_url} />
      <div className="info">
        <div className="name">{profile.name}</div>
        <div className="company"> {profile.company}</div>
      </div>

    </div>);
  }
}

class App extends React.Component {

  constructor(props) {
    super(props);
    this.state = { profiles: [] };
  }
  //state={profiles:testData};

  addNewProfile = (profileData) => {

  //this.setState({profiles:this.state.profiles.concat(profileData)});
  this.setState((prevState)=>({
    profiles:[...prevState.profiles,profileData]
  }))

    console.log("App", profileData);
  }
  render() {
    return (<div className="header">{this.props.title}
      <Form onSubmitProfile={this.addNewProfile}/>
      <CardList profiles={this.state.profiles} />

      {/* <ConditionalStyle /> */}
    </div>);
  }
}

function ConditionalStyle() {

  return <div style={{ color: Math.random() < 0.5 ? "green" : "yellow" }}>How did you like it ?</div>
}
ReactDOM.render(
  <App
    title="The GitHub Cards App"
  />,
  document.getElementById('root')
);
