import React, { useState } from 'react';
import ReactDOM, { render } from 'react-dom';
import './index.css';





function Button (props)
{
  // var [counter, setCounter]=useState(props.value)
  // const handleClick=()=>setCounter(counter+1);
  // return (<button onClick={handleClick} >{counter}</button>)

   const handleClick=()=>props.onClick(props.value);
  return (<button onClick={handleClick} >{props.value}</button>)
}

function Display(props)
{
  <div>{props.value}</div>
}
function App()
{ 
  //return <Button increment1={increment2} value={0}/>
  // return <Button  value={0}/>

  var [counter, setCounter]=useState(0)
  const onClickFunction=(i)=>setCounter(counter+i);
  return <div>
    <Button onClick={onClickFunction}  value={1}/>
    <Button onClick={onClickFunction}  value={5}/>
    <Display>value={counter}</Display>
    </div>
  
}
 
 ReactDOM.render(
  <App />,
   document.getElementById('root')
);

 







