import React, { useState } from 'react';
import ReactDOM, { render } from 'react-dom';
import './index.css';




const PlayNumber = (props) => {

  const NumberClick=()=>{
    console.log('num',props.NumberId);
  }

  var numbercolor=props.availableNumbers.includes(props.numberId)?colors.available:colors.used;
  return (
  // <button key={props.NumberId} className="number" onClick={()=>console.log('num',props.NumberId)}>
  <button key={props.NumberId} className="number" onClick={NumberClick} style={{color:numbercolor}}>
    {props.NumberId}
  </button>
  );
}

const StarsDisplay = (props) => {
  return (
    <>
    {utils.range(1,props.Count).map(starId=>
      <div key={starId} className="star" />)}
    </>
  );
}
const StarMatch = () => {
 const [stars,setStars]=useState(utils.random(1,9));
 const [availableNumbers, setAvailableNumbers]=useState([1,2,3,4,5]);
 const [candidateNumbers, setCandidateNumbers]=useState([2,3]);


 //setAvailableNumbers= n=>{}
 const numbers=9;

  return (
    <div className="game">
      <div className="help">
        Pick 1 or more numbers that sum to the number of stars
      </div>
      <div className="body">
        <div className="left">
              <StarsDisplay Count={stars}/>     
        </div>
        <div className="right">
        {utils.range(1,numbers).map(numberId=>            
           <PlayNumber NumberId={numberId} AvailableNumbers={availableNumbers}/>
         )}           
         
        </div>
      </div>
      <div className="timer">Time Remaining: 10</div>
    </div>
  );
};


// Color Theme
const colors = {
  available: 'lightgray',
  used: 'lightgreen',
  wrong: 'lightcoral',
  candidate: 'deepskyblue',
};
// Math science
const utils = {
  // Sum an array
  sum: arr => arr.reduce((acc, curr) => acc + curr, 0),

  // create an array of numbers between min and max (edges included)
  range: (min, max) => Array.from({ length: max - min + 1 }, (_, i) => min + i),

  // pick a random number between min and max (edges included)
  random: (min, max) => min + Math.floor(Math.random() * (max - min + 1)),

  // Given an array of numbers and a max...
  // Pick a random sum (< max) from the set of all available sums in arr
  randomSumIn: (arr, max) => {
    const sets = [[]];
    const sums = [];
    for (let i = 0; i < arr.length; i++) {
      for (let j = 0, len = sets.length; j < len; j++) {
        const candidateSet = sets[j].concat(arr[i]);
        const candidateSum = utils.sum(candidateSet);
        if (candidateSum <= max) {
          sets.push(candidateSet);
          sums.push(candidateSum);
        }
      }
    }
    return sums[utils.random(0, sums.length - 1)];
  },
};


ReactDOM.render(
  <StarMatch/>,
  document.getElementById('root')
);
