//import React, { useState } from 'react';
import ReactDOM, { render } from 'react-dom';
import React, { Component } from 'react';
import 'bootstrap/dist/css/bootstrap.css';
import "bootstrap/dist/css/bootstrap.min.css";
import '../custom.css'



export class CustomerList extends Component {
  static displayName = CustomerList.name;

  constructor(props) {
    super(props);
    this.state = { customers: [] };
  }
  addNewCustomers = (customerData) => {

    //this.setState({customers:this.state.customers.concat(customerData)});
    this.setState((prevState) => ({
      customers:  customerData
    }))

    console.log("App", customerData);
  }
  render() {
    return (
    <>
    <div className="header">{this.props.title}
      <Form onSubmitProfile={this.addNewCustomers} />
      </div>
      <Customers customers={this.state.customers} />

      {/* <ConditionalStyle /> */}
    
    
    </>);
  }
}

function Customers(props) {

  const allcustomers=props.customers;

  if (allcustomers.length>0)
  {  return (<div>
    {/* <Card {...testData[0]}/>

    <Card {...testData[1]}/>  */}

    {/* {props.customers.map(x => <Customer key={x.customerId}{...x} />)} */}

        

      <table className='table table-striped' aria-labelledby="tabelLabel">
        <thead>
          <tr>
            {/* <th><button className='btn btn-primary'
                              onClick={this.toggleImage}>
                               Image
                          </button>
          </th>
           */}
            <th>Company Name</th>
            <th>Contact Name</th>
            <th>Address</th>
      <th>Phone</th>
          </tr>
        </thead>
        <tbody>
          {allcustomers.map(customer =>
            <tr key={customer.customerId}>
              <td>{customer.companyName}</td>
              <td>{customer.contactName}</td>
              <td>{customer.address}
               {customer.city}, {customer.region}, {customer.country}, {customer.postalCode}
            </td>
            <td>{customer.phone}</td>
            </tr>
          )}
        </tbody>
      </table>

    

  </div>)
  }
}

class Form extends React.Component {

  // userNameInput = React.createRef();
  state = { country: '' };
  handleSubmit = async (evnt) => {
    evnt.preventDefault();
    //console.log(this.userNameInput.current.value);
    // console.log(this.state.userName);
    const resp = await fetch(`http://localhost:5002/api/customers/?country=${this.state.country}`);
    const data = await resp.json();
    console.log(data);
    this.props.onSubmitProfile(data);
    this.setState({ country: '' });
  }

  onTextChange = (evnt) => {
    this.setState({ country: evnt.target.value });
  }
  render() {
    return <form onSubmit={this.handleSubmit}>
      {/* return <form onSubmit={()=>this.props.onSubmit(this.state.userName)}> */}
      <input
        type="text"
        placeholder='Enter a country'
        // ref={this.userNameInput}
        value={this.state.country}
        onChange={this.onTextChange}
        required>
      </input>
      <button>Get Customer</button>
    </form>
  }
}
class Customer extends React.Component {
  render() {
    const customers = this.props;
    return (

      <table className='table table-striped' aria-labelledby="tabelLabel">
        <thead>
          <tr>
            {/* <th><button className='btn btn-primary'
                              onClick={this.toggleImage}>
                               Image
                          </button>
          </th>
           */}
            <th>Company Name</th>
            <th>Contact Name</th>
            {/* <th>Address</th>
      <th>Phone</th> */}
          </tr>
        </thead>
        <tbody>
          {customers.map(customer =>
            <tr key={customer.customerId}>
              <td>{customer.companyName}</td>
              <td>{customer.contactName}</td>
              {/* <td>{customer.address}
               {customer.City}
               {customer.Region}
               {customer.Country}
               {customer.PostalCode}
            </td>
            <td>{customer.Phone}</td> */}
            </tr>
          )}
        </tbody>
      </table>

    );
  }
}
