      </div>

    </div>);
  }
}

class App extends React.Component {

  constructor(props) {
    super(props);
    this.state = { profiles: testData };
  }
  //state={profiles:testData};

  addNewProfile = (profileData) => {

    //this.setState({profiles:this.state.profiles.concat(profileData)});
    this.setState((prevState) => ({
      profiles: [...prevState.profiles, profileData]
    }))

    console.log("App", profileData);
  }
  render() {
    return (<div className="header">{this.props.title}
      <Form onSubmitProfile={this.addNewProfile} />
      <CardList profiles={this.state.profiles} />

      {/* <ConditionalStyle /> */}
    </div>);
  }
}

function ConditionalStyle() {

  return <div style={{ color: Math.random() < 0.5 ? "green" : "yellow" }}>How did you like it ?</div>
}
ReactDOM.render(
