import React, { useState } from 'react';
import ReactDOM from 'react-dom';
import './index.css';




const PlayNumber = (props) => {

  const PlayNumberClick=()=>{
  //  if (props.NumberColor !==colors.available) return;

    props.NumberClick(props.NumberId,props.NumberColor);
    console.log('num',props.NumberId);
  }
  
  return (
  
   <button key={props.NumberId} className="number" onClick={PlayNumberClick} style={{backgroundColor:props.NumberColor}}>  
    {props.NumberId}
  </button>
  );
}

const StarsDisplay = (props) => {
  return (
    <>
    {utils.range(1,props.Count).map(starId=>
      <div key={starId} className="star" />)}
    </>
  );
}
const StarMatch = () => {
 const [stars,setStars]=useState(utils.random(1,9));
 const [availableNumbers, setAvailableNumbers]=useState([1,2,3,4,5,6,7,8,9]);
 const [candidateNumbers, setCandidateNumbers]=useState([]);


 //setAvailableNumbers= n=>{}
 const numbers=9;
 
 const NumberClick=(n,c)=> {
   
  //if (c ==colors.available) return;

  if (c ===colors.used) return;
 
  let copy =c ===colors.wrong?candidateNumbers.filter(cn=>cn!==n):candidateNumbers.concat(n);
  
  
  if (copy.length>0 && (copy.reduce((t, n1) => t + n1) === stars))
  {
    //let copy=availableNumbers.slice().filter(a=>a!==n);
    let intersection = availableNumbers.filter(x => !copy.includes(x));
    setAvailableNumbers(intersection);
    setCandidateNumbers([]);
   setStars(utils.randomSumIn(intersection,9));
  }
  else
  {
    setCandidateNumbers(copy);
  }

}


//incrementCounter=(i)=>setctr(ctr+i)

  return (
    <div className="game">
      <div className="help">
        Pick 1 or more numbers that sum to the number of stars
      </div>
      <div className="body">
        <div className="left">
              <StarsDisplay Count={stars}/>     
        </div>
        <div className="right">
        {utils.range(1,numbers).map(numberId=>                   
         <PlayNumber NumberId={numberId} NumberClick={NumberClick}  NumberColor={GetNumberColor(candidateNumbers, numberId, stars, availableNumbers)}/>
        )}           
         
        </div>
      </div>
      <div className="timer">Time Remaining: 10</div>
    </div>
  );
};


// Color Theme
const colors = {
  available: 'lightgray',
  used: 'lightgreen',
  wrong: 'lightcoral',
  candidate: 'deepskyblue',
};
// Math science
const utils = {
  // Sum an array
  sum: arr => arr.reduce((acc, curr) => acc + curr, 0),

  // create an array of numbers between min and max (edges included)
  range: (min, max) => Array.from({ length: max - min + 1 }, (_, i) => min + i),

  // pick a random number between min and max (edges included)
  random: (min, max) => min + Math.floor(Math.random() * (max - min + 1)),

  // Given an array of numbers and a max...
  // Pick a random sum (< max) from the set of all available sums in arr
  randomSumIn: (arr, max) => {
    const sets = [[]];
    const sums = [];
    for (let i = 0; i < arr.length; i++) {
      for (let j = 0, len = sets.length; j < len; j++) {
        const candidateSet = sets[j].concat(arr[i]);
        const candidateSum = utils.sum(candidateSet);
        if (candidateSum <= max) {
          sets.push(candidateSet);
          sums.push(candidateSum);
        }
      }
    }
    return sums[utils.random(0, sums.length - 1)];
  },
};


ReactDOM.render(
  <StarMatch/>,
  document.getElementById('root')
);


function GetNumberColor(candidateNumbers, numberId, stars, availableNumbers) {
  let numbercolor;
  if (availableNumbers.includes(numberId))
      numbercolor = colors.available;
    else
      numbercolor = colors.used;
  
   if (candidateNumbers.includes(numberId))
    if (candidateNumbers.reduce((t, n) => t + n) > stars)
      numbercolor = colors.wrong;
    else if (candidateNumbers.reduce((t, n) => t + n) < stars)
      numbercolor = colors.candidate;
     else
      numbercolor=colors.used;
    
  
  return numbercolor;
}
