import React from 'react';
import ReactDOM from 'react-dom';
import './index.css';


class Square extends React.Component {

    render() {
      return (
        <button className="square" 
        onClick={()=>{this.props.onClick()}}
        >
          {this.props.value}
        </button>
      );
    }
  }
  
  class Board extends React.Component {
    #nextPlayer='X';
    constructor(props)
    {
        super(props);
        this.state={squares:[null,null,null,null,null,null,null,null,null]}
        
    }
    renderSquare(i) {
      return (<Square 
      value={this.state.squares[i]} 
      onClick={()=>this.handleClick(i)}
      />);
    }

    
    handleClick(i) {
        const squares = this.state.squares.slice();
        //squares[i] = 'X';
      //  squares[i] =nextPlayer=='X'?'X':'O';
        if (this.#nextPlayer=='X')
        {
            squares[i] ='X';
            this.#nextPlayer='O';
        }
        else
        {
            squares[i] ='O';
            this.#nextPlayer='X';
        }
        this.setState({squares: squares});
      }
    render() {
      const status = 'Next player:'+ nextPlayer;
  
      return (
        <div>
          <div className="status">{status}</div>
          <div className="board-row">
            {this.renderSquare(0)}
            {this.renderSquare(1)}
            {this.renderSquare(2)}
          </div>
          <div className="board-row">
            {this.renderSquare(3)}
            {this.renderSquare(4)}
            {this.renderSquare(5)}
          </div>
          <div className="board-row">
            {this.renderSquare(6)}
            {this.renderSquare(7)}
            {this.renderSquare(8)}
          </div>
        </div>
      );
    }
  }
  
  class Game extends React.Component {
    render() {
      return (
        <div className="game">
          <div className="game-board">
            <Board />
          </div>
          <div className="game-info">
            <div>{/* status */}</div>
            <ol>{/* TODO */}</ol>
          </div>
        </div>
      );
    }
  }
  
  // ========================================
  
  ReactDOM.render(
    <Game />,
    document.getElementById('root')
  );
