import React, { Component } from 'react';

export class ProductList extends Component {
  static displayName = ProductList.name;
  static _productUrl = './api/products/products.json';

  constructor(props) {
    super(props);
    this.state = { forecasts: [], loading: true };
  }

  componentDidMount() {
    this.populateWeatherData();
    
  }

  static renderForecastsTable(forecasts) {
    return (
      <table className='table table-striped' aria-labelledby="tabelLabel">
        <thead>
          <tr>
            <th>Product</th>
            <th>Code</th>
            <th>Available</th>
            <th>Price</th>
          </tr>
        </thead>
        <tbody>
          {forecasts.map(forecast =>
            <tr key={forecast.Product}>
              <td>{forecast.Product}</td>
              <td>{forecast.Code}</td>
              <td>{forecast.Available}</td>
              <td>{forecast.Price}</td>
            </tr>
          )}
        </tbody>
      </table>
    );
  }

  render() {
    let contents = this.state.loading
      ? <p><em>Loading...</em></p>
      : ProductList.renderForecastsTable(this.state.forecasts);

    return (
      <div>
        <h1 id="tabelLabel" >Weather forecast</h1>
        <p>This component demonstrates fetching data from the server.</p>
        {contents}
      </div>
    );
  }

  async populateWeatherData() {
   // const response = await fetch('weatherforecast');
   const response = await fetch(this._productUrl);
    const data = await response.json();
    this.setState({ forecasts: data, loading: false });
  }
}
