import React, { useState } from 'react';
import ReactDOM, { render } from 'react-dom';
import './index.css';





// function App(props) {
 
//      return (<div className="header">
// {props.title}
//      </div>);
  
// }

// const App=({title})=>{
//        return (
//        <div className="header"> 
//       {title}
//      </div>);
// }
 
	const testData = [
			{name: "Dan Abramov", avatar_url: "https://avatars0.githubusercontent.com/u/810438?v=4", company: "@facebook"},
      {name: "Sophie Alpert", avatar_url: "https://avatars2.githubusercontent.com/u/6820?v=4", company: "Humu"},
  		{name: "Sebastian Markbåge", avatar_url: "https://avatars2.githubusercontent.com/u/63648?v=4", company: "Facebook"},
	];



function CardList(props){
  return (<div>
    {/* <Card {...testData[0]}/>

    <Card {...testData[1]}/>  */}

    {props.profiles.map(x=><Card {...x}/>)}
    
     </div>)
}

class Form extends React.Component{
  render()
  {
    return <form action="">
      <input type="text" placeholder='GitHub username'></input>
      <button>Add Card</button>
    </form>
  }
}
class Card extends React.Component {
  render() {
    const profile=this.props;
    return (<div className="github-profile" style={{margin:'1rem'}}>
      <img src={profile.avatar_url}/>
      <div className="info">
        <div className="name">{profile.name}</div>
        <div className="company"> {profile.company}</div>
      </div>
     
    </div>);
  }
}

class App extends React.Component {
  render() {
    return (<div className="header">{this.props.title}
    <Form/>
    <CardList profile={testData}/>
    
    {/* <ConditionalStyle /> */}
    </div>);
  }
}

function ConditionalStyle(){

  return <div style={{color:Math.random() <0.5 ? "green" : "yellow"}}>How did you like it ?</div>
}
 ReactDOM.render(  
  <App 
  title="The GitHub Cards App"
  /> ,
  document.getElementById('root')
);
