//import React, { useState } from 'react';
import ReactDOM, { render } from 'react-dom';
import React, { Component } from 'react';
import 'bootstrap/dist/css/bootstrap.css';
import "bootstrap/dist/css/bootstrap.min.css";
import '../custom.css'



export class CustomerList extends Component {
  static displayName = CustomerList.name;

  constructor(props) {
    super(props);
    this.state = { profiles: [] };
  }
  addNewProfile = (profileData) => {

    //this.setState({profiles:this.state.profiles.concat(profileData)});
    this.setState((prevState) => ({
      profiles: [...prevState.profiles, profileData]
    }))

    console.log("App", profileData);
  }
  render() {
    return (<div className="header">{this.props.title}
      <Form onSubmitProfile={this.addNewProfile} />
      <Customers profiles={this.state.profiles} />

      {/* <ConditionalStyle /> */}
    </div>);
  }
}

function Customers(props) {
  return (<div>
    {/* <Card {...testData[0]}/>

    <Card {...testData[1]}/>  */}

    {/* {props.profiles.map(x => <Customer key={x.customerId}{...x} />)} */}

    //const customers = this.props.profiles;
    return (

      <table className='table table-striped' aria-labelledby="tabelLabel">
        <thead>
          <tr>
            {/* <th><button className='btn btn-primary'
                              onClick={this.toggleImage}>
                               Image
                          </button>
          </th>
           */}
            <th>Company Name</th>
            <th>Contact Name</th>
            {/* <th>Address</th>
      <th>Phone</th> */}
          </tr>
        </thead>
        <tbody>
          {customers.map(customer =>
            <tr key={customer.customerId}>
              <td>{customer.companyName}</td>
              <td>{customer.contactName}</td>
              {/* <td>{customer.address}
               {customer.City}
               {customer.Region}
               {customer.Country}
               {customer.PostalCode}
            </td>
            <td>{customer.Phone}</td> */}
            </tr>
          )}
        </tbody>
      </table>

    );

  </div>)
}

class Form extends React.Component {

  // userNameInput = React.createRef();
  state = { country: '' };
  handleSubmit = async (evnt) => {
    evnt.preventDefault();
    //console.log(this.userNameInput.current.value);
    // console.log(this.state.userName);
    const resp = await fetch(`http://localhost:5002/api/customers/?country=${this.state.country}`);
    const data = await resp.json();
    console.log(data);
    this.props.onSubmitProfile(data);
    this.setState({ country: '' });
  }

  onTextChange = (evnt) => {
    this.setState({ country: evnt.target.value });
  }
  render() {
    return <form onSubmit={this.handleSubmit}>
      {/* return <form onSubmit={()=>this.props.onSubmit(this.state.userName)}> */}
      <input
        type="text"
        placeholder='Enter a country'
        // ref={this.userNameInput}
        value={this.state.country}
        onChange={this.onTextChange}
        required>
      </input>
      <button>Get Customer</button>
    </form>
  }
}
class Customer extends React.Component {
  render() {
    const customers = this.props;
    return (

      <table className='table table-striped' aria-labelledby="tabelLabel">
        <thead>
          <tr>
            {/* <th><button className='btn btn-primary'
                              onClick={this.toggleImage}>
                               Image
                          </button>
          </th>
           */}
            <th>Company Name</th>
            <th>Contact Name</th>
            {/* <th>Address</th>
      <th>Phone</th> */}
          </tr>
        </thead>
        <tbody>
          {customers.map(customer =>
            <tr key={customer.customerId}>
              <td>{customer.companyName}</td>
              <td>{customer.contactName}</td>
              {/* <td>{customer.address}
               {customer.City}
               {customer.Region}
               {customer.Country}
               {customer.PostalCode}
            </td>
            <td>{customer.Phone}</td> */}
            </tr>
          )}
        </tbody>
      </table>

    );
  }
}
